{{groovy}}
def obj = doc.getObject("MailArchiveCode.MailClass")
def attachedMails = obj.getProperty('attachedMails')
if (attachedMails != null) {
  attachedMails = attachedMails.value.trim()
  if (attachedMails != "") {
    println ""
    println "{{html clean='false' wiki='true'}}"
    print "**Attached Emails**: "
    attachedMails.split(',').each() { attachedMail ->
      print "<img src=\"/${xwiki.getWebAppPath()}resources/icons/silk/folder.gif\"/> <a href=\"${xwiki.getDocument(attachedMail).getURL()}\">${xwiki.getDocument(attachedMail).title}</a> "
    }
    println "{{/html}}"
    println ""
    println "-----"
  }
}
