// Initialize new mail fields from replied mail
def initNewMailFields(repliedDoc) {

  // Address fields
  def user = xwiki.getDocument(xwiki.user.user.user)
  def from = user.last_name.toUpperCase() + " " + user.first_name + " <" + xwiki.user.email + ">"
  def to = repliedDoc.getValue('from') + ',' + repliedDoc.getValue('to')
  to = to.replaceAll(~/^,(.*)$/, "\$1").replaceAll(~/^(.*),$/,"\$1")
  def cc = "" + repliedDoc.getValue('cc')
  def references = "" + repliedDoc.getValue('') + '\n' + repliedDoc.getValue('references')

  if (to == null) { to = "" }
  if (cc == null) { cc = "" }

  doc.set('from',from)
  doc.set('to',to)
  doc.set('cc',cc)
  doc.set('references', references)
  doc.set('messagesubject', repliedDoc.getValue('messagesubject'))


}

def displayAddressFields(list, field, readonly) {
  println """<div id="div_${field}">"""
  list.split(',').each() { address ->
    address = address.trim().replaceAll(~/[\t\n\x0B\f\r]/, "")
    def id = address.replaceAll(~/ /,"_").replaceAll(~/(<|>)/,"")
    def address_class = "address"
    if (address.startsWith("DL")) {
      address_class += " address_DL"
    }
    print """<span class="address_block"><span class="${address_class}" id="${field}_${id}" name="${field}">${StringEscapeUtils.escapeHtml(address[0..address.indexOf('<')-1])} </span>"""
    if (!readonly) {
      print """<a href="javascript:removeAddress('${field}','${StringEscapeUtils.escapeHtml(address)}')"><img src="${xwiki.getSkinFile('icons/silk/cross.gif')}"/></a></span> """
    } else {
      print """</span> """
    }
  }
  if (!readonly) {
    println """<br/>
<input id="add_${field}" type="text" value=""/>
<a href="javascript:addAddress('${field}')"><img src="${xwiki.getSkinFile('icons/silk/add.gif')}"/></a>"""
  }
  println "</div>"
}

def showHiddenField(field, obj) {
  def html = doc.display(field, "edit", obj)
  html = html.replaceAll(~/\{\{html clean="false" wiki="false"\}\}/, "").replaceAll(~/\{\{\/html\}\}/, "")
  if (html.contains("textarea")) {
    html = html.replaceAll(~/<textarea/, "<textarea class='hidden'")
  } else if (html.contains("input")) {
    html = html.replaceAll(~/<input/, "<input class='hidden'")
  }
  println html
}

